import axios from 'axios';

import {Card, Nav, Col, Container, Navbar, Row, ListGroup} from 'react-bootstrap'
import { useState, useCallback, useEffect } from 'react';


function App() {
  const [rates, setRates] = useState([])
  const fetchAndGetRates = "http://localhost:8080/rates?usedb=true";
  const getRates = "http://localhost:8080/rates?usedb=false";
  const deleteAllRates = "http://localhost:8080/deleteAll";

  const processFetchAndGetRates = async(e) => {
    e.preventDefault();
    try {
      const resp = await axios({
        method: 'get',
        withCredentials: false,
        url: fetchAndGetRates,
        headers: {
          "Access-Control-Allow-Origin": "*"
        },
      });
      console.log(resp.data)
      setRates(resp.data)
    } catch (error) {
      console.log(error)
    }
  }

  const processGetRates = async(e) => {
    if (e !== undefined)
      e.preventDefault();
    try {
      const resp = await axios({
        method: 'get',
        withCredentials: false,
        url: getRates,
        headers: {
          "Access-Control-Allow-Origin": "*"
        },
      });
      console.log(resp.data)
      setRates(resp.data)
    } catch (error) {
      console.log(error)
    }
  }

  const processDeleteAll = async(e) => {
    if (e !== undefined)
      e.preventDefault();
    try {
      await axios({
        method: 'delete',
        withCredentials: false,
        url: deleteAllRates,
        headers: {
          "Access-Control-Allow-Origin": "*"
        },
      });
    } catch (error) {
      console.log(error)
    }
  }

  useEffect((e) => {
    processGetRates(e);    //  get list of rates after downloading
  }, []);

  return (
    <>
      <Navbar bg='dark'>
        <Container>
          <Navbar.Brand>
            <div style={{color: "white"}}>Exchange Rates</div>
          </Navbar.Brand>
          <Nav className="me-auto">
            <Nav.Link onClick={processFetchAndGetRates}>
              <div style={{color: "white"}}>Fetch and Get</div>
            </Nav.Link>
            <Nav.Link onClick={processGetRates}>
              <div style={{color: "white"}}>Get</div>
            </Nav.Link>
            <Nav.Link onClick={processDeleteAll}>
              <div style={{color: "white"}}>Clear</div>
            </Nav.Link>
          </Nav>
        </Container>
      </Navbar>

      <Row>
        <Col>
          <Row>
            <Container style={{width: "100%", height: "100%", padding: 0, margin: 0}}>
                <ListGroup>
                  {rates.map(rate => {
                    return <ListGroup.Item onClick={(e) => console.log(e.currentTarget.getAttribute("ket"))} key={rate.id + 22} as="li">
                      <Row>
                        <Col>
                          Label: {rate.shortName}
                        </Col>
                        <Col>
                          Buy: {rate.valBuy}
                        </Col>
                        <Col>
                          Sell: {rate.valSell}
                        </Col>
                      </Row>
                    </ListGroup.Item>
                  })}
                </ListGroup>
            </Container>
          </Row>
          <Row>

          </Row>
        </Col>

        <Col className="p-0">
          <Container className="p-0" style={{width: "100%", height: "100%"}}>

          </Container>
        </Col>
      </Row>
    </>
  );
}

export default App;
